import React from 'react';
import style from './style.module.css'

export default (props) => {

  const [counter, setCounter] = React.useState(0);

  return ( <
    div className = {
      style.Subscription
    } >
    <
    span > {
      counter
    } < /span> <
    span > {
      props.name
    } < /span> <
    div className = {
      style.ButtonHolder
    } >
    <
    button onClick = {
      () => setCounter(counter + 1)
    } > + < /button> <
    button onClick = {
      () => setCounter(counter - 1)
    } > - < /button> <
    /div> <
    /div>
  )
}
